#!/bin/bash
echo "{ \"response\": [ { \"datetime\": $(grep ro\.build\.date\.utc $OUT/system/build.prop | cut -d= -f2), \"filename\": \"$(basename $(ls $OUT/StagOS*.zip))\", \"id\": \"$((sha256sum $OUT/StagOS*.zip) | cut -d ' ' -f1)\", \"romtype\": $(grep ro\.stag\.releasetype $OUT/system/build.prop | cut -d= -f2), \"size\": $(stat -c%s $OUT/StagOS*.zip), \"url\": \"(Yourwebsite)/$(basename $(ls $OUT/StagOS*.zip))\", \"version\": $(grep ro\.stag\.version $OUT/system/build.prop | cut -d= -f2) " | tee $OUT/update
